mapper.select:
 	select *
	from (
		select rownum as RNUM, list.*
		from(
			select * from qna 
			
				where ${searchCondition} like '%' || #{searchKeyword} || '%'
			
			order by group_no desc, sort_no
			)list
		)
	
	where RNUM>#{firstRecordIndex} 
	and RNUM<=#{firstRecordIndex}+#{recordCountPerPage}

mapper.update:
update qna
set read_count=read_count+1
where qna_no=#{qnaNo}

mapper.select:
select * from(
	select list.*, rownum as RNUM from(
		select * from qna
		where member_no=#{memberNo}
		order by qna_no desc
	)list
)

where RNUM>#{firstRecordIndex} and RNUM<=#{firstRecordIndex}+#{recordCountPerPage}

mapper.update:
		update qna
        set del_flag='Y'
        where qna_no=#{qnaNo}

mapper.select:
	 	select *
		from (
			select rownum as RNUM, list.*
			from(
				select * from qna
                where group_no>#{groupNo} and step=0 and del_flag='N'
                order by group_no
				)list
			)
		
		where RNUM<2

	select *
	
		from (
			select rownum as RNUM, list.*
			from(
				select * from qna
                where group_no<#{groupNo} and step=0 and del_flag='N'
                order by group_no desc
				)list
			)
		where RNUM<2
